import React from 'react';
import { useLocation } from 'react-router-dom';

const UserDashboard = () => {
    const { state } = useLocation();
    const user = state?.user;

    return (
        <div style={pageStyle}>
            <h2 style={headerStyle}>Welcome to Your Dashboard</h2>
            {user ? (
                <div style={infoContainerStyle}>
                    <div style={infoItemStyle}>
                        <strong>Name:</strong> <span>{user.name}</span>
                    </div>
                    <div style={infoItemStyle}>
                        <strong>Location:</strong> <span>{user.location}</span>
                    </div>
                    <div style={infoItemStyle}>
                        <strong>Account Number:</strong> <span>{user.account_number}</span>
                    </div>
                    <div style={infoItemStyle}>
                        <strong>IFSC Code:</strong> <span>{user.ifsc_code}</span>
                    </div>
                </div>
            ) : (
                <p>No user data available.</p>
            )}
        </div>
    );
};

// Styles for the dashboard
const pageStyle = {
    backgroundColor: '#ffffff',
    fontFamily: 'Arial, sans-serif',
    padding: '20px',
    maxWidth: '600px',
    margin: '50px auto',
    borderRadius: '10px',
    boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
};

// Style for the header
const headerStyle = {
    textAlign: 'center',
    marginBottom: '20px',
    color: '#333',
};

// Style for the info container
const infoContainerStyle = {
    backgroundColor: '#f9f9f9',
    borderRadius: '10px',
    padding: '15px',
    boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
};

// Style for each info item
const infoItemStyle = {
    margin: '10px 0',
    padding: '10px',
    borderBottom: '1px solid #e0e0e0',
};

// Remove the bottom border from the last item
infoItemStyle.lastChild = {
    borderBottom: 'none',
};

export default UserDashboard;
