import React, { useState } from 'react';
import axios from 'axios';

const CreateUser = () => {
    const [userData, setUserData] = useState({
        name: '',
        location: '',
        phone_number: '',
    });

    const handleChange = (e) => {
        setUserData({
            ...userData,
            [e.target.name]: e.target.value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://127.0.0.1:8000/api/user/create-user/', { user_profile: userData });
            const { account_number, atm_card_number, ifsc_code } = response.data;
            alert(`User created successfully!\nAccount Number: ${account_number}\nATM Card Number: ${atm_card_number}\nIFSC Code: ${ifsc_code}`);
        } catch (error) {
            alert(error.response?.data?.error || "An error occurred.");
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                name="name"
                placeholder="Name"
                value={userData.name}
                onChange={handleChange}
                required
            />
            <input
                type="text"
                name="location"
                placeholder="Location"
                value={userData.location}
                onChange={handleChange}
                required
            />
            <input
                type="text"
                name="phone_number"
                placeholder="Phone Number"
                value={userData.phone_number}
                onChange={handleChange}
            />
            <button type="submit">Create User</button>
        </form>
    );
};

export default CreateUser;
