import React, { useState } from 'react';
import axios from 'axios';

const Inquiry = () => {
    const [accountNumber, setAccountNumber] = useState('');
    const [balance, setBalance] = useState(null);
    const [error, setError] = useState('');

    const handleInquiry = async (e) => {
        e.preventDefault();
        setError(''); // Clear previous error messages

        try {
            const response = await axios.get(`http://127.0.0.1:8000/api/user/inquiry/${accountNumber}/`);
            setBalance(response.data.balance);
        } catch (err) {
            setError(err.response?.data?.error || 'An error occurred. Please try again.');
            setBalance(null); // Clear the balance if there's an error
        }
    };

    return (
        <div style={pageStyle}>
            <h1 style={headerStyle}>Account Inquiry</h1>
            <form onSubmit={handleInquiry} style={formStyle}>
                <input
                    type="text"
                    placeholder="Account Number"
                    value={accountNumber}
                    onChange={(e) => setAccountNumber(e.target.value)}
                    style={inputStyle}
                    required
                />
                {/* Display the balance here */}
                {balance !== null && <p style={balanceStyle}>Current Balance: ${balance}</p>}
                {error && <p style={errorMessageStyle}>{error}</p>}
                <button type="submit" style={buttonStyle}>Check Balance</button>
            </form>
        </div>
    );
};

// Inline CSS styling
const pageStyle = {
    backgroundColor: '#ffffff',
    fontFamily: 'Arial, sans-serif',
    padding: '20px',
    maxWidth: '500px',
    margin: '50px auto',
    borderRadius: '10px',
    boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
};

const headerStyle = {
    fontSize: '24px',
    color: '#000000',
    fontWeight: 'bold',
    marginBottom: '20px',
    textAlign: 'center',
};

const inputStyle = {
    width: '100%',
    padding: '10px',
    marginBottom: '15px',
    border: '1px solid #ccc',
    borderRadius: '5px',
};

const buttonStyle = {
    width: '100%',
    padding: '10px',
    backgroundColor: '#000000',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px',
};

const formStyle = {
    display: 'flex',
    flexDirection: 'column',
};

const balanceStyle = {
    color: 'green',
    marginTop: '15px',
    textAlign: 'center',
};

const errorMessageStyle = {
    color: 'red',
    marginTop: '15px',
    textAlign: 'center',
};

export default Inquiry;
