import React from 'react';
import { useLocation } from 'react-router-dom';

const UserDashboard = () => {
    const { state } = useLocation();
    const user = state?.user;

    return (
        <div style={pageStyle}>
            {/* Bank Logo */}
            <div style={logoContainerStyle}>
                <img
                    src="https://via.placeholder.com/150?text=Bank+Logo"
                    alt="Bank Logo"
                    style={logoStyle}
                />
            </div>
            {user ? (
                <div style={gridStyle}>
                    <div><strong>Name:</strong> {user.name}</div>
                    <div style={responsiveStyle}><strong>Account Number:</strong> {user.account_number}</div>
                    <div><strong>IFSC Code:</strong> {user.ifsc_code}</div>
                    <div style={responsiveStyle}><strong>Location:</strong> {user.location}</div>
                </div>
            ) : (
                <p>No user data available.</p>
            )}
        </div>
    );
};

// Styles for the dashboard
const pageStyle = {
    backgroundColor: '#ffffff',
    fontFamily: 'Arial, sans-serif',
    padding: '20px',
    maxWidth: '1100px',
    margin: '50px auto',
    borderRadius: '10px',
    boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
    textAlign: 'center',
};

const logoContainerStyle = {
    marginBottom: '20px',
};

const logoStyle = {
    width: '100px',
    height: 'auto',
};

const gridStyle = {
    display: 'grid',
    gridTemplateColumns: '1fr 1fr',
    gap: '20px',
    marginTop: '20px',
    textAlign: 'left',
};

// Style for large screen spacing
const responsiveStyle = {
    marginLeft: '190px',
};

// Responsive adjustments using media queries
const mediaQueries = `
    @media (max-width: 768px) {
        div[style*="gridTemplateColumns"] {
            grid-template-columns: 1fr;
            margin: 0;
        }

        div[style*="marginLeft"] {
            margin-left: 0;
        }

        img[style*="width"] {
            width: 80px;
        }
    }
`;

// Injecting media queries into the page
const styleSheet = document.createElement("style");
styleSheet.type = "text/css";
styleSheet.innerText = mediaQueries;
document.head.appendChild(styleSheet);

export default UserDashboard;
