import React, { useState } from 'react';
import './App.css';
import CreateUser from './components/CreateUser';
import UserLogin from './components/UserLogin';

function App() {
  const [showCreateUser, setShowCreateUser] = useState(true); // Toggle between CreateUser and UserLogin

  const toggleForm = () => {
    setShowCreateUser(!showCreateUser); // Switch between forms
  };

  return (
    <div className="App">
      <div className="form-container">
        <button 
          className={`toggle-button ${showCreateUser ? "to-login" : "to-create"}`} 
          onClick={toggleForm}
        >
          {showCreateUser ? "Go to Login" : "Go to Create User"}
        </button>
        <div className={`form-slide ${showCreateUser ? "show-create" : "show-login"}`}>
          <div className="form-wrapper create-user">
            <CreateUser />
          </div>
          <div className="form-wrapper user-login">
            <UserLogin />
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
