import React, { useState } from 'react';
import axios from 'axios';

const CreateUser = () => {
    const [userData, setUserData] = useState({
        name: '',
        location: '',
        phone_number: '',
    });

    const handleChange = (e) => {
        setUserData({
            ...userData,
            [e.target.name]: e.target.value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://127.0.0.1:8000/api/user/create-user/', { user_profile: userData });
            const { account_number, atm_card_number, ifsc_code } = response.data;
            alert(`User created successfully!\nAccount Number: ${account_number}\nATM Card Number: ${atm_card_number}\nIFSC Code: ${ifsc_code}`);
        } catch (error) {
            alert(error.response?.data?.error || "An error occurred.");
        }
    };

    // Inline CSS styling
    const pageStyle = {
        backgroundColor: '#f5f5f5',
        fontFamily: 'Arial, sans-serif',
        padding: '20px',
        maxWidth: '500px',
        margin: '50px auto',
        borderRadius: '10px',
        boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
        border: '1px solid #ccc'
    };

    const headerStyle = {
        fontSize: '24px',
        color: '#2e5c99',
        fontWeight: 'bold',
        marginBottom: '20px',
        textAlign: 'center',
    };

    const inputStyle = {
        width: '100%',
        padding: '10px',
        marginBottom: '15px',
        border: '1px solid #ccc',
        borderRadius: '5px',
    };

    const buttonStyle = {
        width: '100%',
        padding: '10px',
        backgroundColor: '#2e5c99',
        color: 'white',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer',
        fontSize: '16px',
    };

    const formStyle = {
        display: 'flex',
        flexDirection: 'column',
    };

    const footerText = {
        fontSize: '12px',
        color: '#999',
        textAlign: 'center',
        marginTop: '20px',
    };

    return (
        <div style={pageStyle}>
            <h1 style={headerStyle}>Bank Official - User Creation</h1>
            <form style={formStyle} onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="name"
                    placeholder="Name"
                    style={inputStyle}
                    value={userData.name}
                    onChange={handleChange}
                    required
                />
                <input
                    type="text"
                    name="location"
                    placeholder="Location"
                    style={inputStyle}
                    value={userData.location}
                    onChange={handleChange}
                    required
                />
                <input
                    type="text"
                    name="phone_number"
                    placeholder="Phone Number"
                    style={inputStyle}
                    value={userData.phone_number}
                    onChange={handleChange}
                />
                <button type="submit" style={buttonStyle}>Create User</button>
            </form>
            <p style={footerText}>Please ensure all details are accurate before submission.</p>
        </div>
    );
};

export default CreateUser;
