import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const UserLogin = ({ setUser }) => {
    const [loginData, setLoginData] = useState({
        account_number: '',
        name: ''
    });
    const [loginMessage, setLoginMessage] = useState('');
    const navigate = useNavigate(); // Use navigate instead of history

    const handleChange = (e) => {
        setLoginData({
            ...loginData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoginMessage('');
    
        try {
            const response = await axios.post('http://127.0.0.1:8000/api/user/login/', loginData);
            console.log('Login response:', response.data);
    
         
            const user = response.data.user; 
    
            setUser(user);  
    
            // Navigate to the dashboard and pass the user as state
            navigate('/dashboard', { state: { user } });
        } catch (error) {
            console.error('Login error:', error);
            setLoginMessage(error.response?.data?.error || 'An error occurred during login.');
        }
    };
    

    const pageStyle = {
        backgroundColor: '#ffffff',
        fontFamily: 'Arial, sans-serif',
        padding: '20px',
        maxWidth: '400px',
        margin: '50px auto',
        borderRadius: '10px',
        boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
    };

    const inputStyle = {
        width: '100%',
        padding: '10px',
        marginBottom: '10px',
        borderRadius: '5px',
        border: '1px solid #ccc',
    };

    const buttonStyle = {
        width: '100%',
        padding: '10px',
        backgroundColor: '#000000',
        color: 'white',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer',
        fontSize: '16px',
    };

    const messageStyle = {
        marginTop: '20px',
        color: 'red',
    };

    return (
        <div style={pageStyle}>
            <h2>Bank User Login</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="account_number"
                    placeholder="Account Number"
                    value={loginData.account_number}
                    onChange={handleChange}
                    style={inputStyle}
                    required
                />
                <input
                    type="text"
                    name="name"
                    placeholder="Name"
                    value={loginData.name}
                    onChange={handleChange}
                    style={inputStyle}
                    required
                />
                <button type="submit" style={buttonStyle}>Login</button>
            </form>
            {loginMessage && <p style={messageStyle}>{loginMessage}</p>}
        </div>
    );
};

export default UserLogin;
