import React, { useState } from 'react';
import axios from 'axios';

const Deposit = () => {
    const [accountNumber, setAccountNumber] = useState('');
    const [amount, setAmount] = useState('');
    const [message, setMessage] = useState('');
    const [error, setError] = useState('');

    const handleDeposit = async (e) => {
        e.preventDefault();
        setMessage('');
        setError('');

        try {
            const response = await axios.post('http://127.0.0.1:8000/api/user/deposite/', {
                account_number: accountNumber,
                amount: parseFloat(amount)
            });

            // Handle success response
            setMessage(`Deposit successful! New balance: ${response.data.new_balance}`);
        } catch (error) {
            // Handle error response
            setError(error.response?.data?.error || 'An error occurred during deposit.');
        }
    };

    return (
        
        <div style={pageStyle}>
            <h1 style={headerStyle}>Deposit Money</h1>
            <form onSubmit={handleDeposit} style={formStyle}>
                <input
                    type="text"
                    placeholder="Account Number"
                    value={accountNumber}
                    onChange={(e) => setAccountNumber(e.target.value)}
                    required
                    style={inputStyle}
                />
                <input
                    type="number"
                    placeholder="Amount"
                    value={amount}
                    onChange={(e) => setAmount(e.target.value)}
                    required
                    style={inputStyle}
                />
                <button type="submit" style={buttonStyle}>Deposit</button>
            </form>
            {message && <p style={successText}>{message}</p>}
            {error && <p style={errorText}>{error}</p>}
        </div>
    );
};

// Inline CSS styling
const pageStyle = {
    backgroundColor: '#ffffff',
    fontFamily: 'Arial, sans-serif',
    padding: '20px',
    maxWidth: '500px',
    margin: '50px auto',
    borderRadius: '10px',
    boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
    border: '1px solid #ccc',
};

const headerStyle = {
    fontSize: '24px',
    color: '#000000',
    fontWeight: 'bold',
    marginBottom: '20px',
    textAlign: 'center',
};

const inputStyle = {
    width: '100%',
    padding: '10px',
    marginBottom: '15px',
    border: '1px solid #ccc',
    borderRadius: '5px',
};

const buttonStyle = {
    width: '100%',
    padding: '10px',
    backgroundColor: '#000000',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px',
};

const formStyle = {
    display: 'flex',
    flexDirection: 'column',
};

const successText = {
    color: 'green',
    textAlign: 'center',
    marginTop: '15px',
};

const errorText = {
    color: 'red',
    textAlign: 'center',
    marginTop: '15px',
};

export default Deposit;
