import React, { useState } from 'react';
import axios from 'axios';

const CreateUser = () => {
    const [userData, setUserData] = useState({
        name: '',
        location: '',
        phone_number: '',
        password: ''
    });
    const [message, setMessage] = useState('');

    const handleChange = (e) => {
        setUserData({
            ...userData,
            [e.target.name]: e.target.value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setMessage('');
        try {
            const response = await axios.post('http://127.0.0.1:8000/api/user/create-user/', {
                user_profile: userData,
                password: userData.password
            });
            const { account_number, atm_card_number, ifsc_code } = response.data;
            setMessage(`User created successfully!\nAccount Number: ${account_number}\nIFSC Code: ${ifsc_code}`);
        } catch (error) {
            setMessage(error.response?.data?.error || "An error occurred.");
        }
    };

    const styles = {
        page: {
            backgroundColor: '#f9f9f9',
            fontFamily: 'Arial, sans-serif',
            padding: '30px',
            maxWidth: '600px',
            margin: '50px auto',
            borderRadius: '15px',
            boxShadow: '0px 6px 15px rgba(0, 0, 0, 0.1)',
            border: '1px solid #ddd',
        },
        header: {
            fontSize: '28px',
            color: '#333',
            fontWeight: 'bold',
            marginBottom: '20px',
            textAlign: 'center',
        },
        form: {
            display: 'flex',
            flexDirection: 'column',
            gap: '15px',
        },
        input: {
            width: '100%',
            padding: '12px',
            border: '1px solid #ccc',
            borderRadius: '8px',
            fontSize: '16px',
        },
        button: {
            padding: '12px',
            backgroundColor: '#007bff',
            color: 'white',
            border: 'none',
            borderRadius: '8px',
            cursor: 'pointer',
            fontSize: '16px',
            fontWeight: 'bold',
            transition: 'background-color 0.3s ease',
        },
        buttonHover: {
            backgroundColor: '#0056b3',
        },
        message: {
            marginBottom: '20px',
            padding: '15px',
            backgroundColor: '#e6ffed',
            border: '1px solid #2ecc71',
            borderRadius: '8px',
            color: '#27ae60',
            textAlign: 'center',
        },
        errorMessage: {
            backgroundColor: '#ffebe6',
            border: '1px solid #e74c3c',
            color: '#c0392b',
        },
        footer: {
            fontSize: '12px',
            color: '#888',
            textAlign: 'center',
            marginTop: '20px',
        },
    };

    return (
        <div style={styles.page}>
            {message && (
                <div
                    style={{
                        ...styles.message,
                        ...(message.includes('error') ? styles.errorMessage : {}),
                    }}
                >
                    {message}
                </div>
            )}
            <h1 style={styles.header}>Create New User</h1>
            <form style={styles.form} onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="name"
                    placeholder="Enter Full Name"
                    style={styles.input}
                    value={userData.name}
                    onChange={handleChange}
                    required
                />
                <input
                    type="text"
                    name="location"
                    placeholder="Enter Location"
                    style={styles.input}
                    value={userData.location}
                    onChange={handleChange}
                    required
                />
                <input
                    type="text"
                    name="phone_number"
                    placeholder="Enter Phone Number"
                    style={styles.input}
                    value={userData.phone_number}
                    onChange={handleChange}
                />
                <input
                    type="password"
                    name="password"
                    placeholder="Enter Password"
                    style={styles.input}
                    value={userData.password}
                    onChange={handleChange}
                    required
                />
                <button
                    type="submit"
                    style={styles.button}
                    onMouseEnter={(e) => (e.target.style.backgroundColor = styles.buttonHover.backgroundColor)}
                    onMouseLeave={(e) => (e.target.style.backgroundColor = styles.button.backgroundColor)}
                >
                    Create User
                </button>
            </form>
            <p style={styles.footer}>Please double-check all fields before submitting.</p>
        </div>
    );
};

export default CreateUser;
