import React from 'react';
import { useLocation } from 'react-router-dom';

const UserDashboard = () => {
    const { state } = useLocation();
    const user = state?.user;

    return (
        <div style={pageStyle}>
            <h2 style={{ color: '#ffffff' }}>Welcome to Your Dashboard</h2>
            {user ? (
                <div style={infoStyle}>
                    <div style={infoItemStyle}><strong>Name:</strong> {user.name}</div>
                    <div style={infoItemStyle}><strong>Account Number:</strong> {user.account_number}</div>
                    <div style={infoItemStyle}><strong>IFSC Code:</strong> {user.ifsc_code}</div>
                    <div style={infoItemStyle}><strong>Location:</strong> {user.location}</div>
                </div>
            ) : (
                <p style={{ color: '#ffffff' }}>No user data available.</p>
            )}
        </div>
    );
};

// Styles for the dashboard
const pageStyle = {
    backgroundColor: '#2c2c2c', // Light black background
    fontFamily: 'Arial, sans-serif',
    padding: '20px',
    maxWidth: '600px',
    margin: '50px auto',
    borderRadius: '10px',
    boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
};

const infoStyle = {
    display: 'flex',
    flexDirection: 'column',
    gap: '15px',
    marginTop: '20px',
};

const infoItemStyle = {
    padding: '10px',
    backgroundColor: '#4f4f4f', // Slightly lighter dark gray for items
    borderRadius: '5px',
    boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
    color: '#ffffff', 
};

export default UserDashboard;
