import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const UserLogin = () => {
    const [loginData, setLoginData] = useState({
        account_number: '',
        name: '',
    });
    const [loginMessage, setLoginMessage] = useState('');
    const navigate = useNavigate();

    const handleChange = (e) => {
        setLoginData({
            ...loginData,
            [e.target.name]: e.target.value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoginMessage('');

        try {
            const response = await axios.post('http://127.0.0.1:8000/api/user/login/', loginData);
            const user = response.data.user;

            // Navigate to dashboard with user data
            navigate('/dashboard', {
                state: { user },
            });
        } catch (error) {
            setLoginMessage(error.response?.data?.error || 'An error occurred during login.');
        }
    };

    return (
        <div>
            <h2>User Login</h2>
            <form onSubmit={handleSubmit}>
                <input type="text" name="account_number" placeholder="Account Number" value={loginData.account_number} onChange={handleChange} required />
                <input type="text" name="name" placeholder="Name" value={loginData.name} onChange={handleChange} required />
                <button type="submit">Login</button>
            </form>
            {loginMessage && <p>{loginMessage}</p>}
        </div>
    );
};

export default UserLogin;
